import React, { useState } from 'react';


class LightVersion extends Component {
    constructor(props) {
        super(props) 
            this.state = {
                background: this.props.background,
                hoverBackground: this.props.hoverBackground,
                linkColor: this.props.linkColor,
                navLinks: this.props.navLinks,
                logo: this.props.logo,
                iconActive: this.props.iconActive,
                menuIcon: this.props.menuIcon,
                BadgeActive: this.props.BadgeActive,
                badgeText: this.props.badgeText
            }   
    }
    render() {
        return (
            <nav
            className="responsive-toolbar sidebar"
            style={{ background: this.state.background }}>
            <ul
                style={{ background: this.state.background }}
                className={ this.state.navOpen ? 'active' : '' }
            >
                <figure className="image-logo" onClick={ () => { setNavOpen(!navOpen) } }>
                    <img src={ this.state.logo } height="40px" width="40px" alt="toolbar-logo" />
                </figure>
                { this.state.navLinks.map((link, index) => 
                    <li
                        key={ index }
                        onMouseEnter={ () => { setHoverIndex(index) } }
                        onMouseLeave={ () => { setHoverIndex(-1) } }
                        style={{ background: hoverIndex === index ? (hoverBackground || '#999') : '' }}
                    >
                        <a
                            href={link.path}
                            style={{ color: linkColor }}
                        >   
                            <i className={ link.icon } />
                            { link.text }
                            <Badges BadgeActive={link.BadgeActive}>{link.badgeText}</Badges>
                        </a>
                    </li>
                )}
            </ul>
        </nav>
        );
    }
}

export default LightVersion;
