import React, { Component } from 'react';

class singleEditableSlider extends Component {
    constructor () {

    this.state = {
        value: this.props.value,
        id: this.props.id,
        min: this.props.min,
        max: this.props.max,
        step: this.props.step,
        className: this.props.className,
        disabled: this.props.disabled,
      }
        super ();
        this.onChangeSlider2 = this.onChangeSlider2.bind(this);
    }

      onChangeSlider2(e) {
		var newValue;
		if (e.target && e.target.nodeName === 'INPUT') {
			newValue = e.target.value;
		} else {
			newValue = e.value;
		}

		this.setState({ val2: newValue });
	}

    render() {
        const { id, value, size, min, max, step, ...inputProps } = this.props
        return (
            <div className="editable">
                <div className="current-value inline range-slider">
                    <label>
                        <input type="range" min={this.state.min} disabled={this.state.disabled} className={this.state.className} max={this.state.max} defaultValue={this.state.value} onChange={this.onChangeSlider2} step={this.state.step} />
                        <input type="number" value={this.state.value} onChange={this.onChangeSlider2}/>
                    </label>
                </div>
            </div>
        );
    }
}

export default singleEditableSlider;
