import React, { Component } from 'react';
class LightVersionCards extends Component {
    state = {
        mainTitle: this.props.mainTitle,
        subTitle: this.props.subTitle,
        backIcon: this.props.backIcon,
        boxShadow: this.props.boxShadow,
        width: this.props.width,
        height: this.props.height,
    }
    render() {
        return (
            <div className="cards light-version" style={{boxShadow: this.state.boxShadow}}>
                <div className="heading">
                    <div className="h5">
                        <span style={{width: this.state.width, height: this.state.height, backgroundImage: this.state.backIcon}}></span>
                        {this.state.mainTitle}
                    </div>
                    <p className="body2 text-gray">{this.state.subTitle}</p>
                </div>
                <div className="content">
                    {this.props.children}
                </div>
            </div>
        );
    }
}

export default LightVersionCards;
