import React, { Component } from 'react';
import PropTypes from 'prop-types';


class dropdownmenu extends Component {
  static propTypes = {
    search: PropTypes.string.isRequired

};
static defaultProps = {
  search: false,
};
    constructor(props) {
        super(props);

        this.state = {
          jsxData: [],
          submenu: []

        };
      }
      componentDidMount() {
        this.setState({jsxData: this.createMenuJSX()});
      }
      menuClickEvent(i) {
        let submenu = this.state.submenu;
        let tmpmenu = submenu[i];
        let sub     = tmpmenu.sub.current;
        let caret   = tmpmenu.caret.current;

        if (tmpmenu.active === false) {
          tmpmenu.active = true;
        } else {
          tmpmenu.active = false;
        }

        submenu[i] = tmpmenu;

        this.setState({submenu: submenu});
      }
      createMenuJSX(menu = this.props.menu) {
        let link = [];


        for (let i in menu) {
          let m  = menu[i];
          let number = null;
          let title = null;
          let subtitle = null;
          let icon = null;


          if (typeof m.count !== 'undefined') {
            number = <span className="pull-right">{m.count}</span>;
          }


          if (typeof m.title !== 'undefined') {
              title = <a href={m.link} className="d-headeing" >{icon}<span>{m.title}</span></a>;
          }

          if (typeof m.subtitle !== 'undefined') {
          title = <span>{m.subtitle}</span>;
           }


          if (typeof m.icon !== 'undefined') {
            icon = <span className="dicon">{m.icon}</span>;
          }

          if (typeof m.menu === 'undefined') {
            link.push(
              <li>
                <a href={m.link}>
                  {icon}
                  <span>{m.name}</span>  
                           {number} 
                </a>
              </li>
            );
          } else if (typeof m.menu === 'object') {
            let tmpSubmenu = this.state.submenu;

            tmpSubmenu.push({
              'id': m.link,
              'active': false,
              'caret': React.createRef(),
              'sub': React.createRef()
            });

            link.push(
              <li className="headingdropdown" >
                  {title}
                <ul className="Dropdown-sub-ul" >
                  {this.createMenuJSX(m.menu)}
                </ul>
              </li>
            );

            this.setState({submenu: tmpSubmenu});
          }

        }

        return link;
      }


    render() {
      const { search,Count,number } = this.props;

      function Searchhtml(props){
        if(props.active == true){
              return <li><div class="dropdownsearch"><input type="text" placeholder="Type here..." value="" /><button></button></div></li>
          }
          else{
            return null;
          }
       }

        return (
            <div className="dropdown open">

                <ul className="dropdown-menu p-relative">
                  <Searchhtml active={search}/>
                {this.state.jsxData}
                {this.props.children}
                </ul>
            </div>
        );
    }
}

export default dropdownmenu;
