import React, { Component } from 'react';
import Header from '../header/header';
import CardsPreview from './Types/CardsPreview'; 
import  './Cards.css';
import CardBg from '../../images/Card_Container.png'

class Cards extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Cards & Payments' }
            ],
            boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)",
            width: "100%", 
            height: "200px",
            cardNo: "4321 0987 0000 9876",
            title1: "current balance",
            title2: "Benjamin Parker",
            date: "03/24",
            borderRadius: "8px",
            price: "$3 458.90",
            icon: "Visa",
            className: "cards flex-div",
            opacity: "0.64"

        }
    }
    render() {
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Cards Preview</div>
                                <div className="row">
                                    <CardsPreview { ...this.state } background="#1a1f71" textColor="#fff"/>
                                    <CardsPreview { ...this.state } background="#ffffff" textColor="#000a19"/>
                                    <CardsPreview { ...this.state } boxShadow="none" background={`url(${CardBg})no-repeat / 100%`} textColor="#fff"/>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Cards;
