import React from 'react'
import Header from '../header/header';

class Index extends React.Component {

  constructor (props) {
    super(props)
    this.state = {
      header: [
        { title: 'Carousel Sliders' }
      ],
      value: 0,
      slides1: [
        (<img src={Slider1} alt='carousel'/>),
        (<img src={Slider2} alt='carousel'/>),
        (<img src={Slider3} alt='carousel'/>),
        (<img src={Slider1} alt='carousel'/>),
        (<img src={Slider2} alt='carousel'/>),
        (<img src={Slider3} alt='carousel'/>),
      ],
      slides2: [
        (<img src={Slider1} alt='carousel'/>),
        (<img src={Slider2} alt='carousel'/>),
        (<img src={Slider3} alt='carousel'/>),
        (<img src={Slider1} alt='carousel'/>),
        (<img src={Slider2} alt='carousel'/>),
        (<img src={Slider3} alt='carousel'/>),
      ],
    }
    this.onchange = this.onchange.bind(this);
  }
  render () {
    return (
      <div className="wrapper">
        <section className="container">
          <div className="row">
            <Header header={this.state.header[0].title} />  
            <div className="row w-100">
              <div className="col-md-12 mb-30">
                <p className="body1 mb-30">You can create carousel slider using images, videos, posts or product previews. Sliders have custom height and width.</p>
                <div className="Carousel01">
                  <Carousel infinite centered slidesPerPage={4}  arrows Dots slides={this.state.slides1}   value={this.state.value} onChange={this.onchange} />
                  <CarouselDots value={this.state.value} onChange={this.onchange} number={this.state.slides1.length} />
                  {/* <CarouselDots
                    value={this.state.value}
                    onChange={this.onChange}
                    thumbnails={[
                      (<img key={1} className="img-example-small"  src={Slider1} alt='carousel'/>),
                      (<img key={2} className="img-example-small" src={Slider2} alt='carousel' />),
                      (<img key={2} className="img-example-small" src={Slider3} alt='carousel' />),
                      (<img key={4} className="img-example-small" src={Slider1} alt='carousel' />),
                      (<img key={5} className="img-example-small" src={Slider2} alt='carousel' />),
                      (<img key={6} className="img-example-small" src={Slider3} alt='carousel' />),
                    ]}
                  /> */}
                </div>
                <div className="Carousel02">
                  <Carousel infinite arrows Dots slides={this.state.slides2}   value={this.state.value} onChange={this.onchange} />
                  <CarouselDots value={this.state.value} onChange={this.onchange} number={this.state.slides2.length} />
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    )
  }
}

export default Index;
