import React from 'react'
import Header from '../header/header';
import SimpleCarousel from './Carousel'
import Dots from './Indicatore'
import Buttons from './CarouselButton'
import CenterCarousel from './CenterCarousel'
import './Carousel.css'
import Slider1 from '../../images/carousel/rsz_slide1.png'
import Slider2 from '../../images/carousel/rsz_slide2.png'
import Slider3 from '../../images/carousel/rsz_slide3.png'

// Main App
class Index extends React.Component {

  constructor (props) {
    super(props)
    this.state = {
      header: [
        { title: 'Carousel Sliders' }
      ],
      axis: 'x',
      width: this.props.width,
      height: this.props.height,
      className: this.props.className
    }
    this.setAxis = axis => {
      return () => this.setState({'axis': axis})
    }
  }
  render () {
    return (
      <div className="wrapper">
        <section className="container">
          <div className="row">
            <Header header={this.state.header[0].title} />  
            <div className="row w-100">
              <div className="col-md-12 mb-30">
                <p className="body1 mb-30">You can create carousel slider using images, videos, posts or product previews. Sliders have custom height and width.</p>
                <div id="carousel01">
                  {/* <header>
                    <span className={this.state.axis === 'x' ? 'axis current' : 'axis'}
                      onClick={this.setAxis('x')}>horizontal</span>
                    <span className={this.state.axis === 'y' ? 'axis current' : 'axis'}
                      onClick={this.setAxis('y')}>vertical</span>
                  </header> */}
                  <SimpleCarousel centerSlidePercentage={50} centerMode={true} center loop axis={this.state.axis} widgets={[Dots, Buttons]} className="Carousel">
                    <p style={{backgroundColor: 'royalblue', height: '100%'}}><img src={Slider1} alt='carousel'/></p>
                    <p style={{backgroundColor: 'yellow', height: '100%'}}><img src={Slider2} alt='carousel'/></p>
                    <p style={{backgroundColor: 'red', height: '100%;'}}><img src={Slider3} alt='carousel'/></p>
                    <p style={{backgroundColor: 'royalblue', height: '100%'}}><img src={Slider1} alt='carousel'/></p>
                    <p style={{backgroundColor: 'yellow', height: '100%'}}><img src={Slider2} alt='carousel'/></p>
                    <p style={{backgroundColor: 'red', height: '100%;'}}><img src={Slider3} alt='carousel'/></p>
                  </SimpleCarousel>
                </div>
                <div className="center-carousel">
                {imgList.length === 0 && <div>Loading...</div>}
                {imgList.length > 0 &&
                  <Carousel imgList={imgList} img_width={300} img_height={300}
                  visibleImages={3} duration={750}/>
                }
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    )
  }
}

export default Index;
