import React, { Component } from 'react';
import Header from '../header/header';
import LightVersion from '../Sidebar/Types/LightVersion';
import UserSidebarCopy from './Types/LightVersion/UserSidebarcopy'
import { grid } from 'react-icons-kit/feather/grid';
import {star} from 'react-icons-kit/feather/star'
import {user} from 'react-icons-kit/feather/user'
import {flash} from 'react-icons-kit/fa/flash'
import {ic_tune} from 'react-icons-kit/md/ic_tune'
import { Icon } from 'react-icons-kit'
import './Sidebar.css'

class Index extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Sidebars' }
            ],
            navLinks: [
                {
                    text: 'Dashboard',
                    // path: '/',
                    menuIcon: <Icon icon={grid}/>,
                    iconActive: true,
                    active: true,
                    items: [
                        {
                            text: 'item11',
                            active: true,
                        },
                        {
                            text: 'item12',
                            active: false,
                        },
                        {
                            text: 'item13',
                            active: false,
                        },
                    ],
                },
                {
                    text: 'People',
                    // path: '/contact',
                    menuIcon: <Icon icon={user}/>,
                    iconActive: true,
                    active: false,
                },
                {
                    text: 'Favourites',
                    // path: '/about',
                    menuIcon: <Icon icon={star}/>,
                    iconActive: true,
                    active: false,
                },
                {
                    text: 'Updates',
                    // path: '/blog',
                    BadgeActive: true,
                    badgeText: '+10',
                    badgeBg: 'red',
                    menuIcon: <Icon icon={flash}/>,
                    iconActive: true,
                    active: false,
                },
                {
                    text: 'Setting',
                    // path: '/portfolio',
                    menuIcon: <Icon icon={ic_tune}/>,
                    iconActive: true,
                    active: false,
                },
            ],
            otherLinks: [
                {
                    text: 'my collections',
                    className: "myCollection",
                    items: [
                      {
                        text: 'Mood Inspiration',
                        active: false,
                      },
                      {
                        text: 'International Women’s Day — Editorial Selects',
                        active: false
                      },
                      {
                        text: 'Visual Assets for Marketers',
                        active: false
                      }
                    ]

                }
            ]
        }
    }
    render() {
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Light Version</div>
                                <div className="row mb-50">
                                    <div className="col-md-4">
                                        <LightVersion 
                                            BrandActive={false}
                                            navLinks={this.state.navLinks}
                                            otherLinks={this.state.otherLinks}
                                            // logo={ logo }
                                            background="#fff"
                                            hoverText="#fff"
                                            hoverBackground="var(--primary)"
                                            // hoverText="var(--white)"
                                            // linkColor="#777"
                                            badgeBg="yellow"
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <UserSidebarCopy navLinks={this.state.navLinks} otherLinks={this.state.otherLinks} 
                                            BrandActive={true}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Index;
