import React, { Component, useState } from 'react';
import UserAvatar from '../../../Notification/avitar/avitar-dropdown/avitar_dropdownhtml';
import AvitarIcon from '../../../../images/avitar.png';
import more from '../../../../images/More_Vertical.svg';
import {grid} from 'react-icons-kit/feather/grid';
import { Icon } from 'react-icons-kit';

const navLinks = [
  { url: '/about-us', name: 'About Us' },
  { url: '/projects', name: 'Projects' },
  { url: '/services', name: 'Services' },
  { url: '/contact-us', name: 'Contact Us' },
];

class UserSidebar extends Component {
  constructor(){
    super();
    this.state = {
      style:"menu",
      menuStatus:"open"
    };
    this.handleClick = this.handleClick.bind(this);
  };

  handleClick() {
    switch(this.state.menuStatus)
    {
      case "open":
        this.setState({
          menuStatus:"close",
          style:"menu active"
        });
        break;
      case "close":
        this.setState({
          menuStatus:"open",
          style:"menu"
        });
        break;

    }        
  }

  render() {
    return (      
      <div>
        <button onClick={this.handleClick}>menu</button>
        <div className={this.state.style}>               
          <ul>
            {navLinks.map(({ url, name }) => (
              <li>
                <a href={url}>{name}</a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default UserSidebar;
