import React, { Component } from 'react';

class singleEditableSlider extends Component {
    state = {
        value: this.props.value,
        id: this.props.id,
        min: this.props.min,
        max: this.props.max,
        step: this.props.step,
        className: this.props.className,
        disabled: this.props.disabled,
      }

      handleOnChange = (e) => {
        this.setState({ value: e.target.value });
      }
    render() {
        const { id, value, size, min, max, step, ...inputProps } = this.props
        return (
            <div className="row">
                <div className="col-md-3">
                    <div className={`${this.state.className ? this.state.className : ''} editable`}>
                        <div className="current-value inline">
                            <div ref={ref => this.slider = ref} id="range-slider" className={`${this.state.className} double-range-slider`}>
                                <div ref={ref => this.minValue = ref} id="min" data-content={this.state.currentMin}>
                                    <div ref={ref => this.minValueDrag = ref} id="min-drag" onMouseDown ={this.changeMinValue} onTouchStart={this.changeMinValue}></div>
                                </div>

                                <div ref={ref => this.maxValue = ref} id="max" data-content={this.state.currentMax}>
                                    <div ref={ref => this.maxValueDrag = ref} id="max-drag" onMouseDown={this.changeMaxValue} onTouchStart={this.changeMaxValue}></div>
                                </div>

                            </div> 
                            <input 
                            id="max-input" 
                            type="number" 
                            className="right"
                            onChange={this.setMax} 
                            value={this.state.inputMax}
                            min={this.minForMax}
                            max={this.state.maxV}/>

                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default singleEditableSlider;
