import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Card from '../../Magazines/Cardtags/card/card';
import Cardimg from '../../Magazines/Cardtags/cardimage/Cardimg';
import Cardoverlay from '../../Magazines/Cardtags/Cardoverlay/Cardoverlay';
import Detail from '../../Magazines/Cardtags/carddetail/carddetail';
import Button from '../../button/buttonprop/buttonprop';
import Cardheading from '../../Magazines/Cardtags/Carddescription/cardheading';
import CardDT from '../../Magazines/Cardtags/Carddescription/cardDT';
import Action from '../../Magazines/Cardtags/Cardaction/Cardaction';
import CardFooter from '../../Magazines/Cardtags/Cardfooter/Cardfooter';
import { Icon } from 'react-icons-kit'
import C10 from '../../../images/carousel/Card_Carousel.png';
import {heart} from 'react-icons-kit/feather/heart'
import { thumbsUp } from 'react-icons-kit/feather/thumbsUp';
import { messageSquare } from 'react-icons-kit/feather/messageSquare';
import Carousel from '../CarouselWrapper';
import CarouselDots from '../CarouselDots';
import '../Carousel.css'

class NumberCountCarousel extends Component {
    static propTypes = {
        className: PropTypes.string,
        minDraggableOffset: PropTypes.number,
        breakpoints: PropTypes.objectOf(PropTypes.shape({
        arrows: PropTypes.bool,
        arrowLeft: PropTypes.element,
        arrowRight: PropTypes.element,
        addArrowClickHandler: PropTypes.bool,
        className: PropTypes.string,
        })),
    }
    /**
   * Adds onClick handler to the arrow if possible (if it does not already have one)
   * @param {ReactElement} element to render
   * @param {function} onClick handler to be added to element
   * @param {string} name of an element
   * @param {boolean} disable info whether the arrow is disabled
   * @return {ReactElement} element with added handler
   */
  renderArrowWithAddedHandler = (element, onClick, name, disable = false) => (
    <div
      className={
        classnames(
          'Carousel__customArrows',
          {
            'Carousel__arrow--disable': disable,
          },
          `Carousel__custom-${name}`,
        )
      }
      ref={el => this[`${name}Node`] = el}
      onClick={this.getProp('addArrowClickHandler') ? onClick : null}
    >
      {element}
    </div>
  );

  /**
   * Renders arrow left
   * @return {ReactElement} element
   */
  renderArrowLeft = () => {
    const value = this.getCurrentValue();
    const disabled = value <= 0 && !this.getProp('infinite');

    if (this.getProp('arrowLeft')) {
      if (!disabled) {
        return this.renderArrowWithAddedHandler(this.getProp('arrowLeft'), this.prevSlide, 'arrowLeft');
      }
      const arrow = this.getProp('arrowLeftDisabled') ? this.getProp('arrowLeftDisabled') : this.getProp('arrowLeft');
      return this.renderArrowWithAddedHandler(arrow, this.prevSlide, 'arrowLeft', disabled);
    }
    if (this.getProp('arrows')) {
      return (
        <button
          className="Carousel__arrows Carousel__arrowLeft"
          onClick={this.prevSlide}
          ref={el => this.arrowLeftNode = el}
          disabled={disabled}
        >
          <span>prev</span>
        </button>
      );
    }
    return null;
  };

  /**
   * Renders arrow right
   * @return {ReactElement} element
   */
  renderArrowRight = () => {
    const slides = this.getChildren();
    const value = this.getCurrentValue();
    const lastSlideIndex = slides.length - 1;
    const disabled = value === lastSlideIndex && !this.getProp('infinite');

    if (this.getProp('arrowRight')) {
      if (!disabled) {
        return this.renderArrowWithAddedHandler(this.getProp('arrowRight'), this.nextSlide, 'arrowRight');
      }
      const arrow = this.getProp('arrowRightDisabled') ? this.getProp('arrowRightDisabled') : this.getProp('arrowRight');
      return this.renderArrowWithAddedHandler(arrow, this.nextSlide, 'arrowRight', disabled);
    }
    if (this.getProp('arrows')) {
      return (
        <button
          className="Carousel__arrows Carousel__arrowRight"
          onClick={this.nextSlide}
          ref={el => this.arrowRightNode = el}
          disabled={disabled}
        >
          <span>next</span>
        </button>
      );
    }
    return null;
  };
    constructor (props) {
        super(props)
        this.state = {
            value: 0,
            slides: [
            (<Card style="z1 text-center text-white img-card ">
                <Cardimg img={C10}></Cardimg>
                <Cardoverlay >
                    <Detail>
                        <div className="card-flex mt-5">
                            <div className="flex"></div>
                            <div className="flex">
                                <Button
                                    size=""
                                    style="btn-white text"
                                    Centericon={true}
                                    icon={<Icon icon={heart} size={18} style={{backgroundColor: '#018aae',padding: '11px', borderRadius: '20px'}}/>}
                                />
                            </div>
                        </div>
                    </Detail>
                </Cardoverlay>
            </Card>),
            (<Card style="z1 text-center text-white img-card ">
                <Cardimg img={C10}></Cardimg>
                <Cardoverlay >
                    <Detail>
                        <div className="card-flex mt-5">
                            <div className="flex"></div>
                            <div className="flex">
                                <Button
                                    size=""
                                    style="btn-white text"
                                    Centericon={true}
                                    icon={<Icon icon={heart} size={18} style={{backgroundColor: '#018aae',padding: '11px', borderRadius: '20px'}}/>}
                                />
                            </div>
                        </div>
                    </Detail>
                </Cardoverlay>
            </Card>),
            (<Card style="z1 text-center text-white img-card ">
                <Cardimg img={C10}></Cardimg>
                <Cardoverlay >
                    <Detail>
                        <div className="card-flex mt-5">
                            <div className="flex"></div>
                            <div className="flex">
                                <Button
                                    size=""
                                    style="btn-white text"
                                    Centericon={true}
                                    icon={<Icon icon={heart} size={18} style={{backgroundColor: '#018aae',padding: '11px', borderRadius: '20px'}}/>}
                                />
                            </div>
                        </div>
                    </Detail>
                </Cardoverlay>
            </Card>),
            ],
        }
        this.onchange = this.onchange.bind(this);
    }
    onchange(value) {
        this.setState({ value });
    }
    render() {
        return (
            <>  
                <div className="col-md-4">
                    <div className="card-carousel">
                        <Carousel infinite slides={this.state.slides} value={this.state.value} onChange={this.onchange} />
                        <div className="slide-count">
                            {this.renderArrowLeft()}
                            {this.renderArrowRight()}
                            <CarouselDots value={this.state.value} onChange={this.onchange} number={this.state.slides.length} />/
                            <div className="total">{this.state.slides.length}</div>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default NumberCountCarousel;
