import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Radiobtnhtml extends Component {
    static propTypes = {
        hasError: PropTypes.bool,
        id: PropTypes.string.isRequired,
        label: PropTypes.string.isRequired,
      };
      static defaultProps = {
        hasError: false,

      };
      handleRadioChange = (e) => {
        // const value = e.target.value;

        // this.setState({ 
        //     // value: e.target.value, 
        //     value: e.target.value= "677898"
        // });
        //     //alue: this.state.value
        //     // value: this.state.value === 0 ? '2398 9845 0003 6745' ? this.state.value === 1 ? '4321 0987 000 9876' : '', 
        //     // value: this.state.value === 0 ? 'amercian' : this.state.value === 1 ? 'mastercard' : 'frgtdfg',
        //     console.log(this.state.value)
        console.log(e.target.id)
        this.setState({value: e.target.id})
    }

    render() {
        const { id, label,width,height,  hasError, ...inputProps } = this.props;
        const checkboxClassname = `
        m-radio 

          ${hasError && 'm-checkbox--has-error'}
        `;

        const inputClassname = `
          m-radio__input


          ${hasError && 'm-checkbox--has-error__input'}
        `;

        const labelClassname = `
        m-radio__label
      `;
        return (
            <>

            <div className={checkboxClassname}>

               <input
                 type="radio"
                 className={inputClassname}
                 ref={el => (this.selector = el)}
                 id={id}
                 {...inputProps} onClick={this.handleRadioChange.bind(this)}
               />
               <label className={labelClassname} htmlFor={id}>{label}</label>      
            </div>

       </>
        );
    }
}

export default Radiobtnhtml;
