import React, { Component } from 'react';
import Header from '../header/header';
import LightVersion from '../Sidebar/Types/LightVersion';
import UserSidebarCopy from './Types/LightVersion/UserSidebarcopy'
import UserCollapseSidebar from './Types/LightVersion/UserCollapseSidebar'
import { grid } from 'react-icons-kit/feather/grid';
import {star} from 'react-icons-kit/feather/star'
import {user} from 'react-icons-kit/feather/user'
import {flash} from 'react-icons-kit/fa/flash'
import {ic_tune} from 'react-icons-kit/md/ic_tune'
import { Icon } from 'react-icons-kit'
import {folder} from 'react-icons-kit/feather/folder'
import {ic_help_outline} from 'react-icons-kit/md/ic_help_outline'
import './Sidebar.css'

class Index extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Sidebars' }
            ],
            navLinks: [
                {
                    text: 'Dashboard',
                    // path: '/',
                    menuIcon: <Icon icon={grid}/>,
                    iconActive: true,
                    active: false,
                    items: [
                        {
                            text: 'item11',
                            active: true,
                        },
                        {
                            text: 'item12',
                            active: false,
                        },
                        {
                            text: 'item13',
                            active: false,
                        },
                    ],
                },
                {
                    text: 'People',
                    // path: '/contact',
                    menuIcon: <Icon icon={user}/>,
                    iconActive: true,
                    active: false,
                    items: [
                        {
                          text: 'item11',
                          active: true,
                        },
                        {
                          text: 'item12',
                          active: false,
                        },
                        {
                          text: 'item13',
                          active: false,
                        },
                    ],
                },
                {
                    text: 'Favourites',
                    // path: '/about',
                    menuIcon: <Icon icon={star}/>,
                    iconActive: true,
                    active: false,
                },
                {
                    text: 'Updates',
                    // path: '/blog',
                    BadgeActive: true,
                    badgeText: '+10',
                    badgeBg: 'red',
                    menuIcon: <Icon icon={flash}/>,
                    iconActive: true,
                    active: false,
                },
                {
                    text: 'Setting',
                    // path: '/portfolio',
                    menuIcon: <Icon icon={ic_tune}/>,
                    iconActive: true,
                    active: false,
                },
            ],
            otherLinks: [
                {
                    text: 'my collections',
                    items: [
                      {
                        text: 'Mood Inspiration',
                        menuIcon: <Icon icon={folder} style={{color: 'var(--primary-variant)'}}/>,
                        iconActive: true,
                        active: false,
                        items: [
                            {
                              text: 'Mood Inspirationdsfdsfsdf',
                              menuIcon: <Icon icon={folder} style={{backgroundColor: 'red'}}/>,
                              iconActive: true,
                              active: false,
                            },
                            {
                              text: 'International Women’s Day — Editorial Selects',
                              menuIcon: <Icon icon={folder} fill="red"/>,
                              iconActive: true,
                              active: false
                            },
                            {
                              text: 'Visual Assets for Marketers',
                              menuIcon: <Icon icon={folder} fill="red"/>,
                              iconActive: true,
                              active: false
                            }
                          ]
                      },
                      {
                        text: 'International Women’s Day — Editorial Selects',
                        menuIcon: <Icon icon={folder} style={{color: 'var(--secondary)'}}/>,
                        iconActive: true,
                        active: false
                      },
                      {
                        text: 'Visual Assets for Marketers',
                        menuIcon: <Icon icon={folder} style={{color: 'var(--La-Rioja)'}}/>,
                        iconActive: true,
                        active: false
                      }
                    ]

                }
            ],
            moreLinks: [
                {
                    text: 'my collections',
                    items: [
                      {
                        text: 'Mood Inspirationdsfdsfsdf',
                        menuIcon: <Icon icon={folder} style={{backgroundColor: 'red'}}/>,
                        iconActive: true,
                        active: false,
                      },
                      {
                        text: 'International Women’s Day — Editorial Selects',
                        menuIcon: <Icon icon={folder} fill="red"/>,
                        iconActive: true,
                        active: false
                      },
                      {
                        text: 'Visual Assets for Marketers',
                        menuIcon: <Icon icon={folder} fill="red"/>,
                        iconActive: true,
                        active: false
                      }
                    ]

                }
            ],
            bottomFixed: [
                {
                    text: 'Need Help?',
                    menuIcon: <Icon icon={ic_help_outline} fill="red" size={20} style={{backgroundColor: '#f2f2f2', width: '40px',height: '40px',borderRadius: '40px',display: 'flex'}}/>,
                    iconActive: true,                  
                }
            ]
        }
    }
    render() {
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Light Version</div>
                                <div className="row mb-50">
                                    {/* <div className="col-md-4">
                                        <LightVersion 
                                            BrandActive={false}
                                            navLinks={this.state.navLinks}
                                            otherLinks={this.state.otherLinks}
                                            // logo={ logo }
                                            background="#fff"
                                            hoverText="#fff"
                                            hoverBackground="var(--primary)"
                                            // hoverText="var(--white)"
                                            // linkColor="#777"
                                            badgeBg="yellow"
                                        />
                                    </div> */}
                                    <div className="col-md-4">
                                        <UserCollapseSidebar
                                            navLinks={this.state.navLinks} 
                                            otherLinks={this.state.otherLinks} 
                                            moreLinks={this.state.moreLinks}
                                            OtherClassName="my-collection"
                                            BrandActive={true}
                                            bottomFixed={this.state.bottomFixed}
                                            active={false}
                                            collapseExpand="expand"
                                        />
                                    </div>
                                    <div className="col-md-1">
                                        <UserCollapseSidebar
                                            navLinks={this.state.navLinks} 
                                            otherLinks={this.state.otherLinks} 
                                            moreLinks={this.state.moreLinks}
                                            OtherClassName="my-collection"
                                            BrandActive={true}
                                            bottomFixed={this.state.bottomFixed}
                                            active={false}
                                            collapseExpand="collapsed"
                                         />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Index;
