import React, { Component, useState } from 'react';
import UserAvatar from '../../../Notification/avitar/avitar-dropdown/avitar_dropdownhtml';
import AvitarIcon from '../../../../images/avitar.png';
import more from '../../../../images/More_Vertical.svg';
import {grid} from 'react-icons-kit/feather/grid';
import { Icon } from 'react-icons-kit';

class UserSidebar extends Component {
  render() {
    // const {itemIcon} = this.state.pages.map((icon) => <span>{icon.itemIcon}</span>);
    // const BadgeValue = () => {
    //   return <> {this.state.pages.map((value,i) => <>{value.badgeText}</>)}</>
    // }
    const MenuIcon = (props) => {
      if(props.IconActive === true) {
          return <>{this.state.pages.map((icon,i) => <>{icon.itemIcon}</>)}</>
      }else {
          return null
      }
    }
    const Badges = (props) => {
        if(props.BadgeActive === true) {
            return <>{this.state.pages.map((value,i) => <>{value.badgeText}</>)}</>
        }else {
            return null
        }
    }
    return (
      <div className="sidebar">
        <BoxContainer className={`sidebar-menu ${toggleText(this.state.sideNav.active, 'active', '')}`}>
            {/* Brand and sidebar toggle button */}
            <TextAndButton
              className="sidebar-head"
              btnState={this.state.sideNav.active}
              handleClick={this.toggleSideNav}  
              text="Brand Name"
              brandName={false}
            />
            <div className="sidebar-content">
              <ListComponent
                data={this.state.pages}
                Component={
                  (item, itemIndex)=>(
                    <div className={`menu-item ${toggleText(item.active, 'active', '')}`}>
                      <a href="#/"
                          onClick={()=>this.toggleMenu(this.state.pages, item.name)}
                      ><MenuIcon IconActive={item.IconActive}>{item.itemIcon} </MenuIcon> { item.title }<Badges BadgeActive={item.BadgeActive}>{item.badgeText}</Badges></a>
                        {/* {
                          item.items &&
                          <ul className="sub-menu">
                              <ListComponent
                                data={item.items}
                                Component={
                                    (subitem)=>(
                                    <li className={`${toggleText(subitem.active, 'active', '')}`}>
                                        <a href="#/"
                                        onClick={()=>this.toggleMenu(item.items, subitem.name, itemIndex)}
                                        >{ subitem.name }</a>
                                    </li>
                                    )
                                }
                              />
                          </ul>
                      } */}
                    </div>
                  )
                }
              />
              <ListComponent
                data={this.state.myCollection}
                Component={
                  (item, itemIndex)=>(
                    <div className={`my-collection ${toggleText(item.active, 'active', '')}`}>
                      <span className="sub-title" onClick={()=>this.toggleMenu2(this.state.myCollection, item.name)}>{ item.title }</span>
                        {
                          item.items &&
                          <ul>
                              <ListComponent
                                data={item.items}
                                Component={
                                    (subitem)=>(
                                    <li className={`${toggleText(subitem.active, 'active', '')}`}>
                                        <a href="#/"
                                        onClick={()=>this.toggleMenu2(item.items, subitem.name, itemIndex)}
                                        >{ subitem.name }</a>
                                    </li>
                                    )
                                }
                              />
                          </ul>
                      }
                    </div>
                  )
                }
              />
            </div>
        </BoxContainer>
      </div>
    );
  }
}

export default UserSidebar;
