import React, { Component } from 'react';
import Header from '../header/header';
import CardsPreview from './Types/CardsPreview'; 
import LightVersionCard from './Types/LightVersionCards';
import Radiobtns from '../../components/input/radionbtn/radiobtnhtml/Radiobtnhtml'
import  './Cards.css';
import CardBg from '../../images/Card_Container.png'
import CardBg2 from '../../images/Card_Container2.png'
import VisaIcon from '../../images/visa-icon.svg';
import VisaFillIcon from '../../images/visafill-icon.svg';
import AmeExpIcon from '../../images/ame-icon.svg';
import MasterCardIcon from '../../images/mastercard-icon.svg';
import ArrowBack from '../../images/Arrow_Back.svg';
import MoreIcon from '../../images/More_Vertical.svg';


class Cards extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Cards & Payments' }
            ],
            cardType: "",
            boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)",
            width: "100%", 
            height: "200px",
            cardNo: "4321 0987 0000 9876",
            title1: "current balance",
            title2: "Benjamin Parker",
            date: "03/24",
            borderRadius: "8px",
            price: "$3 458.90",
            icon: VisaIcon,
            className: "cards flex-div",
            opacity: "0.64",

        }
    }
    handleChange = (e) => {
        if(e.target.checked) {
            this.props.handleChange(e.target.value === 1 ? 2 ? 0 : 1 : 2)
        }
    }

    render() {
        const CARD_TYPE = {
            2: VisaIcon,
            1: MasterCardIcon,
            0: AmeExpIcon
        }
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Cards Preview</div>
                                <div className="row mb-50">
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background="#1a1f71" 
                                            textColor="#fff"
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background="#ffffff"
                                            textColor="#000a19" 
                                            icon={VisaFillIcon}
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background={`url(${CardBg})no-repeat`} 
                                            textColor="#fff" 
                                            className="hasBg-img cards flex-div"
                                        />
                                    </div>
                                </div>
                                <div className="h5 mb-20">Light Version</div>
                                <div className="row">
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="My Cards"
                                            subTitle="Change your default payment method"
                                            {...this.state}
                                            visibility= "none"
                                        >
                                            <CardsPreview  
                                                { ...this.state } 
                                                background="#000" 
                                                textColor="#fff"
                                                icon={CARD_TYPE[this.state.cardType]}
                                            />
                                            <ul>
                                                <li>
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="am" onChange={this.handleChange} defaultChecked={this.state.cardType === "americanexpress"} value="americanexpress" label="2398 9845 0003 6745" name="cards"/>
                                                </li>
                                                <li>
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="mac" onChange={this.handleChange} defaultChecked={this.state.cardType === "mastercard"} value="mastercard" label="4321 0987 000 9876" name="cards"/>
                                                </li>
                                                <li>
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="visa" onChange={this.handleChange} checked={this.state.cardType === "visa"} value="visa" label="0987 0000 0033 8723" name="cards" />
                                                </li>
                                            </ul>
                                            <button>Add New Card {this.state.cardType ? <div>Hello</div> : null}</button>
                                        </LightVersionCard>
                                    </div>
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="Card Information"
                                            backIcon={ArrowBack}
                                            moreIcon={MoreIcon}
                                            {...this.state}
                                            >
                                            <CardsPreview  
                                                price="myBank" 
                                                width="100%" 
                                                icon={AmeExpIcon} 
                                                borderRadius="8px" 
                                                height="200px" 
                                                cardNo="4321 09•• •••• 9876" 
                                                date="03/24" 
                                                className="hasBg-img cards flex-div" 
                                                background={`url(${CardBg2})no-repeat`} 
                                                textColor="#fff"
                                            />
                                        </LightVersionCard>
                                    </div>
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="Payment Details"
                                            {...this.state}
                                            visibility="none"
                                        >
                                            <CardsPreview  
                                                { ...this.state }
                                            />
                                        </LightVersionCard>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Cards;
