import React, { useState } from 'react';
import UserAvatar from '../../Notification/avitar/avitar-dropdown/avitar_dropdownhtml';
import AvitarIcon from '../../../images/avitar.png';
import Tags from '../../ChipsTags/Types/Tags'

function ResponsiveNavigation({ background, hoverBackground,hoverText, linkColor, navLinks,otherLinks, logo, handleClick,BrandActive,badgeBg,badgeStyle}) {

    const [ navOpen, setNavOpen ] = useState(0)
    const [ hoverIndex, setHoverIndex ] = useState(-1)
    let [Status, setStatus] = useState('online');
    let selectStatus = (e) => {
        setStatus(e.target.value)
    }
    const BrandSection = (props) => {
        if(props.BrandActive === true) {
            return  <img src={ logo } height="40px" width="40px" alt="toolbar-logo" />
        }else {
            return <UserAvatar
            title="Benjamin Parker"  
            subtitle={
                <span className={`${Status} status`}>
                    <select onChange={selectStatus}>
                        <option value="online">Online</option>
                        <option value="offline">Offline</option>
                        <option value="away">Away</option>
                        <option value="dnd">Don't disturb</option>
                    </select>
                </span>
            }  
            avitar={AvitarIcon} hasRound haslg> 
            <img className="" src={AvitarIcon} alt="User Avatar"/>
          </UserAvatar> 
        }
    }
    const Badges = (props) => {
      if(props.BadgeActive === true) {
          return <span className="badge"><Tags DivclassName="form-group tag number mb-20" LabelclassName={`${props.badgeBg}`} Title={props.badgeText} /></span>
      }else {
          return null
      }
    }
    const MenuIcon = (props) => {
        if(props.iconActive === true) {
            // return <Icon Icon={props.menuIcon} />
            return <span className="menu-icon">{props.menuIcon}</span>
        }else {
            return null
        }
    }

    return (
        <div className="sidebar">
            <nav
                className="responsive-toolbar sidebar-menu"
                style={{ background: background }}>
                {/* for toggle class on click for menu icon  onClick={ () => { setNavOpen(!navOpen) } } */}
                <div className="sidebar-head">
                    <BrandSection BrandActive={BrandActive}></BrandSection>
                </div>
                <div className="sidebar-content">
                    <ul
                        style={{ background: background }}
                        className={`${ navOpen ? 'active' : '' } sidebar-menu`}
                    >
                        { navLinks.map((link, index) => 
                            <li
                                key={ index }
                                onMouseEnter={ () => { setHoverIndex(index) } }
                                onMouseLeave={ () => { setHoverIndex(-1) } }
                                style={{ background: hoverIndex === index ? (hoverBackground || '#999') : '' }}
                                className="menu-item"
                            >
                                <a
                                    key={ index }
                                    onMouseEnter={ () => { setHoverIndex(index) } }
                                    onMouseLeave={ () => { setHoverIndex(-1) } }
                                    href={link.path}
                                    style={{ color: hoverIndex === index ? (hoverText || 'var(--white)') : 'var(--black)' }}
                                >   
                                    <MenuIcon iconActive={link.iconActive} menuIcon={link.menuIcon}></MenuIcon>
                                    { link.text }
                                    <Badges BadgeActive={link.BadgeActive} badgeText={link.badgeText} badgeBg={badgeBg} badgeStyle={badgeStyle}></Badges>
                                </a>
                            </li>
                        )}
                        {otherLinks.map((link))}
                    </ul>
                    <ul>

                    </ul>
                </div>
            </nav>
        </div>
    )
}

export default ResponsiveNavigation
