import React, { Component } from 'react';
import Header from '../header/header';
import CardsPreview from './Types/CardsPreview'; 
import LightVersionCard from './Types/LightVersionCards';
import Radiobtns from '../../components/input/radionbtn/radiobtnhtml/Radiobtnhtml'
import  './Cards.css';
import CardBg from '../../images/Card_Container.png'
import CardBg2 from '../../images/Card_Container2.png'
import VisaIcon from '../../images/visa-icon.svg';
import VisaFillIcon from '../../images/visafill-icon.svg';
import AmeExpIcon from '../../images/ame-icon.svg';
import MasterCardIcon from '../../images/mastercard-icon.svg';
import ArrowBack from '../../images/Arrow_Back.svg';
import MoreIcon from '../../images/More_Vertical.svg';


class Cards extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Cards & Payments' }
            ],
            boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)",
            width: "100%", 
            height: "200px",
            cardNo: "4321 0987 0000 9876",
            title1: "current balance",
            title2: "Benjamin Parker",
            date: "03/24",
            borderRadius: "8px",
            price: "$3 458.90",
            icon: VisaIcon,
            className: "cards flex-div",
            opacity: "0.64",
            cardType: VisaIcon,
            value: '',
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        console.log('clicked...');

        this.setState ({
            cardNo: this.state.value === 0 ? '4321 0987 000 9876' :  '0987 0000 0033 8723'
        });
    }

    render() {
        var value = "";
        if (this.state.value === 1) {
            value = '4321 0987 000 9876'
        } else if(this.state.value === 2) {
            value = '2398 9845 0003 6745'
        }else {
            value = '0987 0000 0033 8723'
        }
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Cards Preview</div>
                                <div className="row mb-50">
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background="#1a1f71" 
                                            textColor="#fff"
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background="#ffffff"
                                            textColor="#000a19" 
                                            icon={VisaFillIcon}
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <CardsPreview 
                                            { ...this.state } 
                                            background={`url(${CardBg})no-repeat`} 
                                            textColor="#fff" 
                                            className="hasBg-img cards flex-div"
                                        />
                                    </div>
                                </div>
                                <div className="h5 mb-20">Light Version</div>
                                <div className="row">
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="My Cards"
                                            subTitle="Change your default payment method"
                                            {...this.state}
                                            visibility= "none"
                                        >
                                            <CardsPreview  
                                                { ...this.state } 
                                                background="#000" 
                                                textColor="#fff"
                                                // icon={this.state.cardType}
                                                icon={this.state.value}
                                                cardNo={this.state.cardNo}
                                            /> {console.log(this.state.cardNo)}
                                            <ul>
                                                <li>    
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="am" onChange={this.handleChange} defaultChecked={this.state.cardType === AmeExpIcon}  value={this.state.value === 1} label="2398 9845 0003 6745" name="cards"/>
                                                </li>value
                                                <li>
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="mac" onChange={this.handleChange} defaultChecked={this.state.cardType === MasterCardIcon}  value={this.state.value === 2} label="4321 0987 000 9876" name="cards"/>
                                                </li>
                                                <li>
                                                    <Radiobtns style={{width: "16px", height: "16px"}} id="visa" onChange={this.handleChange} defaultChecked={this.state.cardType === VisaIcon}  value={this.state.value === 0} label="0987 0000 0033 8723" name="cards" />
                                                </li>
                                            </ul>
                                            <button>Add New Card</button>
                                        </LightVersionCard>
                                    </div>
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="Card Information"
                                            backIcon={ArrowBack}
                                            moreIcon={MoreIcon}
                                            {...this.state}
                                            >
                                            <CardsPreview  
                                                price="myBank" 
                                                width="100%" 
                                                icon={AmeExpIcon} 
                                                borderRadius="8px" 
                                                height="200px" 
                                                cardNo="4321 09•• •••• 9876" 
                                                date="03/24" 
                                                className="hasBg-img cards flex-div" 
                                                background={`url(${CardBg2})no-repeat`} 
                                                textColor="#fff"
                                            />
                                        </LightVersionCard>
                                    </div>
                                    <div className="col-md-4">
                                        <LightVersionCard 
                                            mainTitle="Payment Details"
                                            {...this.state}
                                            visibility="none"
                                        >
                                            <CardsPreview  
                                                { ...this.state }
                                            />
                                        </LightVersionCard>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Cards;
