//         iconActive: true,
//     },
//     {
//         text: 'Updates',
//         // path: '/blog',
//         BadgeActive: true,
//         badgeText: '+10',
//         menuIcon: <Icon icon={flash}/>,
//         iconActive: true,
//     },
//     {
//         text: 'Setting',
//         // path: '/portfolio',
//         menuIcon: <Icon icon={ic_tune}/>,
//         iconActive: true,
//     },
// ];
// const myCollection = [
//     {
//         text: 'my collections',
//         items: [
//           {
//             text: 'Mood Inspiration',
//             active: false,
//           },
//           {
//             text: 'International Women’s Day — Editorial Selects',
//             active: false
//           },
//           {
//             text: 'Visual Assets for Marketers',
//             active: false
//           }
//         ]

//     }
// ]
class Index extends Component {
    constructor(props) {
        super(props)
        this.state = {
            header: [
                { title: 'Sidebars' }
            ],
            navLinks: [
                {
                    text: 'Dashboard',
                    // path: '/',
                    menuIcon: <Icon icon={grid}/>,
                    iconActive: true,
                    active: false,
                      items: [
                        {
                          text: 'item11',
                          active: true,
                        },
                        {
                          text: 'item12',
                          active: false,
                        },
                        {
                          name: 'item13',
                          active: false,
                        },
                      ],

                },
                {
                    text: 'People',
                    // path: '/contact',
                    menuIcon: <Icon icon={user}/>,
                    iconActive: true,
                },
                {
                    text: 'Favourites',
                    // path: '/about',
                    menuIcon: <Icon icon={star}/>,
                    iconActive: true,
                },
                {
                    text: 'Updates',
                    // path: '/blog',
                    BadgeActive: true,
                    badgeText: '+10',
                    badgeBg: 'red',
                    menuIcon: <Icon icon={flash}/>,
                    iconActive: true,
                },
                {
                    text: 'Setting',
                    // path: '/portfolio',
                    menuIcon: <Icon icon={ic_tune}/>,
                    iconActive: true,
                },
            ],
            otherLinks: [
                {
                    text: 'my collections',
                    className: "myCollection",
                    items: [
                      {
                        text: 'Mood Inspiration',
                        active: false,
                      },
                      {
                        text: 'International Women’s Day — Editorial Selects',
                        active: false
                      },
                      {
                        text: 'Visual Assets for Marketers',
                        active: false
                      }
                    ]

                }
            ]
        }
    }
    render() {
        return (
            <div className="wrapper">
                <section className="container">
                    <div className="row">
                        <Header header={this.state.header[0].title} />  
                        <div className="row w-100">
                            <div className="col-md-12 mb-30">
                                <div className="h5 mb-20">Light Version</div>
                                <div className="row mb-50">
                                    <div className="col-md-4">
                                        <LightVersion 
                                            BrandActive={false}
                                            navLinks={this.state.navLinks}
                                            otherLinks={this.state.otherLinks}
                                            // logo={ logo }
                                            background="#fff"
                                            hoverText="#fff"
                                            hoverBackground="var(--primary)"
                                            // hoverText="var(--white)"
                                            // linkColor="#777"
                                            badgeBg="yellow"
                                        />
                                    </div>
                                    <div className="col-md-4">
                                        <UserSidebarCopy navLinks={this.state.navLinks} otherLinks={this.state.otherLinks} />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        );
    }
}

export default Index;
