import React, { Component, useState } from 'react';
import UserAvatar from '../../../Notification/avitar/avitar-dropdown/avitar_dropdownhtml';
import AvitarIcon from '../../../../images/avitar.png';
import more from '../../../../images/More_Vertical.svg';
import {grid} from 'react-icons-kit/feather/grid';
import { Icon } from 'react-icons-kit';

const toggleCollectionProperty = ({
  arrCollection, 
  targetId, 
  itemIdProp,
  itemProp, 
  itemVal, 
  itemOppVal,
}) => {
  const tplCollection = [...arrCollection];
  for(let i = 0, l=tplCollection.length; i < l; i += 1) {
    if(tplCollection[i][itemIdProp] === targetId) {
      tplCollection[i][itemProp] = itemVal;
    } else {
      tplCollection[i][itemProp] = itemOppVal;
    }
  }

  return tplCollection;
};


const withActiveProp = Component => props => {
  return (
    props.active && <Component
      {...props}
    />
  );
};

const toggleText = (bool, val1, val2) => {
  return bool ? val1 : val2;
};


const ListComponent = ({ data: listData, Component, className }) => {

  if (!listData) {

    return false;

  }

  return (
    <React.Fragment>
      {

        Component && listData.map((item, itemIndex) => (
          <React.Fragment key={item.id}>
            { Component(item, itemIndex) }
          </React.Fragment>
        ))
      }
    </React.Fragment>
  );

};


const BoxContainer = ({ className, children }) => {
  return (
    <div className={`${className?className:''}`}>
      { children }
    </div>
  );
};

const TextAndButton = ({ className, btnState, brandName, text, handleClick }) => {
  function BrandName(props){
    if(props.active == true){
    return <h1 className={`${toggleText(className, className, '')}--text`}>{ text }</h1>

      }
      else{
        return null;
      }
  }

  let [Status, setStatus] = useState('online');
  let selectStatus = (e) => {
    setStatus(e.target.value)
  }
  // console.log(Status);
  return (
    <div className={`${toggleText(className, className, '')}`}>
      <UserAvatar
        onClick={handleClick}
        title="Benjamin Parker"  
        subtitle={
          <span className={`${Status} status`}>
            <select onChange={selectStatus}>
              <option value="online">Online</option>
              <option value="offline">Offline</option>
              <option value="away">Away</option>
              <option value="dnd">Don't disturb</option>
            </select>
          </span>
        }  
        avitar={AvitarIcon} hasRound haslg> 
        <img className="" src={AvitarIcon} alt="User Avatar"/>
        {
          toggleText(btnState) 
        }
      </UserAvatar> 
      <div className="more">
        <img src={more} alt="More Icon"/>
        {/* <button onClick={handleClick}>
          {
            toggleText(btnState, 'Close', 'Open') 
          }
        </button> */}
      </div>
      <BrandName active={brandName} />
      {/* <button onClick={handleClick}>
        {
          toggleText(btnState, 'Close', 'Open') 
        }
      </button> */}
    </div>
  );
};
class UserSidebar extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      sideNav: {
        active: true,
      },
      pages: [
        {
          name: 'Dashboard',
          title: 'Dashboard',
          itemIcon: '../../../../images/am.png',
          IconActive: true,
          active: false,
        //   items: [
        //     {
        //       name: 'item11',
        //       active: true,
        //     },
        //     {
        //       name: 'item12',
        //       active: false,
        //     },
        //     {
        //       name: 'item13',
        //       active: false,
        //     },
        //   ],
        },
        {
          name: 'People',
          title: 'People',
          active: true,
        },
        {
          name: 'Favorites',
          title: 'Favorites',
          badgeText: '+10',
          BadgeActive: true,
          active: false,
        },
        {
          name: 'Updates',
          title: 'Updates',
          badgeText: '+10',
          BadgeActive: true,
          active: false
        },
        {
          name: 'Uploads',
          title: 'Uploads',
        },
        {
          name: 'Settings',
          title: 'Settings',
        }
      ],  
      myCollection: [
        {
          name: 'my collections',
          title: 'my collections',
          items: [
            {
              name: 'Mood Inspiration',
              active: false,
            },
            {
              name: 'International Women’s Day — Editorial Selects',
              active: false
            },
            {
              name: 'Visual Assets for Marketers',
              active: false
            }
          ]

        }
      ]
    }; 
    this.toggleSideNav = this.toggleSideNav.bind(this);
    this.toggleMenu = this.toggleMenu.bind(this);
    this.toggleMenu2 = this.toggleMenu2.bind(this);
  }


  toggleSideNav() {
    const { sideNav } = this.state;
    sideNav.active = !this.state.sideNav.active
    this.setState({ sideNav });
  }


  toggleMenu(collection, collTargetId, submenuIndex) {
    let Menu = '';
    if (typeof submenuIndex !== 'number' && !submenuIndex) {
      Menu = toggleCollectionProperty ({
        arrCollection: collection, 
        targetId: collTargetId, 
        itemIdProp: 'name',
        itemProp: 'active', 
        itemVal: true, 
        itemOppVal: false,
      });
    } else {
      const submenu = toggleCollectionProperty ({
        arrCollection: collection, 
        targetId: collTargetId, 
        itemIdProp: 'name',
        itemProp: 'active', 
        itemVal: true, 
        itemOppVal: false,
      });
      Menu = this.state.pages;
      Menu[submenuIndex].items = submenu;   
    }

    this.setState({ Menu });
  }
  toggleMenu2 (collection, collTargetId, submenuIndex) {
    let Menu = '';
    if (typeof submenuIndex !== 'number' && !submenuIndex) {
      Menu = toggleCollectionProperty ({
        arrCollection: collection, 
        targetId: collTargetId, 
        itemIdProp: 'name',
        itemProp: 'active', 
        itemVal: true, 
        itemOppVal: false,
      });
    } else {
      const submenu = toggleCollectionProperty ({
        arrCollection: collection, 
        targetId: collTargetId, 
        itemIdProp: 'name',
        itemProp: 'active', 
        itemVal: true, 
        itemOppVal: false,
      });
      Menu = this.state.pages;
      Menu[submenuIndex].items = submenu;   
    }

    this.setState({ Menu });
  }

  render() {
    // const {itemIcon} = this.state.pages.map((icon) => <span>{icon.itemIcon}</span>);
    // const BadgeValue = () => {
    //   return <> {this.state.pages.map((value,i) => <>{value.badgeText}</>)}</>
    // }
    const MenuIcon = (props) => {
      if(props.IconActive === true) {
          return <span>{this.state.pages.map((value,i) => <span style={{background: 'url(' + value.itemIcon + ')'}}>{}</span>)}</span>
      }else {
          return null
      }
    }
    const Badges = (props) => {
        if(props.BadgeActive === true) {
            return <span>{this.state.pages.map((value,i) => <>{value.badgeText}</>)}</span>
        }else {
            return null
        }
    }
    return (
      <div className="sidebar">
        <BoxContainer className={`sidebar-menu ${toggleText(this.state.sideNav.active, 'active', '')}`}>
            {/* Brand and sidebar toggle button */}
            <TextAndButton
              className="sidebar-head"
              btnState={this.state.sideNav.active}
              handleClick={this.toggleSideNav}  
              text="Brand Name"
              brandName={false}
            />
            <div className="sidebar-content">
              <ListComponent
                data={this.state.pages}
                Component={
                  (item, itemIndex)=>(
                    <div className={`menu-item ${toggleText(item.active, 'active', '')}`}>
                      <a href="#/"
                          onClick={()=>this.toggleMenu(this.state.pages, item.name)}
                      ><MenuIcon key={itemIndex} IconActive={item.IconActive}></MenuIcon>{ item.title } <Badges key={item.itemIndex} BadgeActive={item.BadgeActive}></Badges></a>
                        {/* {
                          item.items &&
                          <ul className="sub-menu">
                              <ListComponent
                                data={item.items}
                                Component={
                                    (subitem)=>(
                                    <li className={`${toggleText(subitem.active, 'active', '')}`}>
                                        <a href="#/"
                                        onClick={()=>this.toggleMenu(item.items, subitem.name, itemIndex)}
                                        >{ subitem.name }</a>
                                    </li>
                                    )
                                }
                              />
                          </ul>
                      } */}
                    </div>
                  )
                }
              />
              <ListComponent
                data={this.state.myCollection}
                Component={
                  (item, itemIndex)=>(
                    <div className={`my-collection ${toggleText(item.active, 'active', '')}`}>
                      <span className="sub-title" onClick={()=>this.toggleMenu2(this.state.myCollection, item.name)}>{ item.title }</span>
                        {
                          item.items &&
                          <ul>
                              <ListComponent
                                data={item.items}
                                Component={
                                    (subitem)=>(
                                    <li className={`${toggleText(subitem.active, 'active', '')}`}>
                                        <a href="#/"
                                        onClick={()=>this.toggleMenu2(item.items, subitem.name, itemIndex)}
                                        >{ subitem.name }</a>
                                    </li>
                                    )
                                }
                              />
                          </ul>
                      }
                    </div>
                  )
                }
              />
            </div>
        </BoxContainer>
      </div>
    );
  }
}

export default UserSidebar;
