import React, { useState } from 'react';

function ResponsiveNavigation({ background, hoverBackground, linkColor, navLinks, logo, badgeText, menuIcon}) {

    const [ navOpen, setNavOpen ] = useState(0)
    const [ hoverIndex, setHoverIndex ] = useState(-1)
    const Badges = (props) => {
      if(props.BadgeActive === true) {
          return <span>{props.badgeText}</span>
      }else {
          return null
      }
    }
    const MenuIcon = (props) => {
        if(props.IconActive === true) {
            return <span>{props.badgeText}</span>
        }else {
            return null
        }
      }

    return (
        <nav
            className="responsive-toolbar sidebar"
            style={{ background: background }}>
            <ul
                style={{ background: background }}
                className={ navOpen ? 'active' : '' }
            >
                <figure className="image-logo" onClick={ () => { setNavOpen(!navOpen) } }>
                    <img src={ logo } height="40px" width="40px" alt="toolbar-logo" />
                </figure>
                { navLinks.map((link, index) => 
                    <li
                        key={ index }
                        onMouseEnter={ () => { setHoverIndex(index) } }
                        onMouseLeave={ () => { setHoverIndex(-1) } }
                        style={{ background: hoverIndex === index ? (hoverBackground || '#999') : '' }}
                    >
                        <a
                            href={link.path}
                            style={{ color: linkColor }}
                        >   
                            <i className={ link.icon } />
                            { link.text }
                            <Badges BadgeActive={link.BadgeActive} badgeText={link.badgeText}></Badges>
                        </a>
                    </li>
                )}
            </ul>
        </nav>
    )
}

export default ResponsiveNavigation
