import React, { Component } from 'react';
import Carousel from './CarouselWrapper';
import CarouselDots from './CarouselDots';
import './Carousel.css'
import Slider1 from '../../images/carousel/rsz_slide1.png'
import Slider2 from '../../images/carousel/rsz_slide2.png'
import Slider3 from '../../images/carousel/rsz_slide3.png'
class CenteredCarousel extends Component {
    constructor (props) {
        super(props)
        this.state = {
          value: 0,
          slides1: [
            (<img src={Slider1} alt='carousel'/>),
            (<img src={Slider2} alt='carousel'/>),
            (<img src={Slider3} alt='carousel'/>),
            (<img src={Slider1} alt='carousel'/>),
            (<img src={Slider2} alt='carousel'/>),
            (<img src={Slider3} alt='carousel'/>),
          ],
          slides2: [
            (<img src={Slider1} alt='carousel'/>),
            (<img src={Slider2} alt='carousel'/>),
            (<img src={Slider3} alt='carousel'/>),
            (<img src={Slider1} alt='carousel'/>),
            (<img src={Slider2} alt='carousel'/>),
            (<img src={Slider3} alt='carousel'/>),
          ],
        }
        this.onchange = this.onchange.bind(this);
      }
      onchange(value) {
        this.setState({ value });
      }
    render() {
        return (
            <>
                <Carousel infinite centered slidesPerPage={4}  arrows Dots slides={this.state.slides1}   value={this.state.value} onChange={this.onchange} />
                <CarouselDots value={this.state.value} onChange={this.onchange} number={this.state.slides1.length} />
            </>
        );
    }
}
export default CenteredCarousel;
