import React, { Component, useState } from 'react';
import UserAvatar from '../../Notification/avitar/avitar-dropdown/avitar_dropdownhtml'
import AvitarIcon from '../../../images/avitar.png';
import more from '../../../images/More_Vertical.svg'

const toggleCollectionProperty = ({
    arrCollection, 
    targetId, 
    itemIdProp,
    itemProp, 
    itemVal, 
    itemOppVal,
  }) => {
    const tplCollection = [...arrCollection];
    for(let i = 0, l=tplCollection.length; i < l; i += 1) {
      if(tplCollection[i][itemIdProp] === targetId) {
        tplCollection[i][itemProp] = itemVal;
      } else {
        tplCollection[i][itemProp] = itemOppVal;
      }
    }

    return tplCollection;
  };


  const withActiveProp = Component => props => {
    return (
      props.active && <Component
        {...props}
      />
    );
  };

  const toggleText = (bool, val1, val2) => {
    return bool ? val1 : val2;
  };


  const ListComponent = ({ data: listData, Component, className }) => {

    if (!listData) {

      return false;

    }

    return (
      <React.Fragment>
        {

          Component && listData.map((item, itemIndex) => (
            <React.Fragment key={item.id}>
              { Component(item, itemIndex) }
            </React.Fragment>
          ))
        }
      </React.Fragment>
    );

  };


  const BoxContainer = ({ className, children }) => {
    return (
      <div class={`${className?className:''}`}>
        { children }
      </div>
    );
  };

  const TextAndButton = ({ className,icon,Lefticon,Righticon, btnState, brandName, text, handleClick }) => {
    function BrandName(props){
      if(props.active == true){
      return <h1 className={`${toggleText(className, className, '')}--text`}>{ text }</h1>

        }
        else{
          return null;
        }
    }
    function Lefticons(props){
      if(props.active == true){
            return <span className="lefticon">
                          {icon}
                      </span>
        }
        else{
          return null;
        }

    }
    function Righticons(props){
      if(props.active == true){
            return <span className="righticon">
                          {icon}
                      </span>
        }
        else{
          return null;
        }

    }
    let [Status="online", setStatus] = useState('online');
    let selectStatus = (e) => {
      setStatus(e.target.value)
    }
    console.log(Status);
    return (
      <div className={`${toggleText(className, className, '')}`}>
        <UserAvatar 
          title="Benjamin Parker"  
          subtitle={
            <span className={`${Status} status`}>
              <select onChange={selectStatus}>
                <option value="online">Online</option>
                <option value="offline">Offline</option>
                <option value="away">Away</option>
                <option value="dnd">Don't disturb</option>
              </select>
            </span>
          }  
          avitar={AvitarIcon} hasRound haslg> 
          <img className="" src={AvitarIcon} alt="User Avatar"/>

        </UserAvatar> 
        <div className="more">
          <img src={more} alt="More Icon"/>
        </div>
        <BrandName active={brandName} />
        {/* <button onClick={handleClick}>
          {
            toggleText(btnState, 'Close', 'Open') 
          }
        </button> */}
      </div>
    );
  };

class LightVersion extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          sideNav: {
            active: true,
          },
          pages: [
            {
              name: 'item1',
              title: 'item1',
              active: true,
              items: [
                {
                  name: 'item11',
                  active: true,
                },
                {
                  name: 'item12',
                  active: false,
                },
                {
                  name: 'item13',
                  active: false,
                },
              ],
            },
            {
              name: 'item2',
              title: 'item2',
              active: false,
              items: [
                {
                  name: 'item21',
                  active: true,
                },
                {
                  name: 'item22',
                  active: false,
                },
                {
                  name: 'item23',
                  active: false,
                },
              ],
            },
            {
              name: 'item3',
              title: 'item3',
              active: false,
              items: [
                {
                  name: 'item31',
                  active: true,
                },
                {
                  name: 'item32',
                  active: false,
                },
                {
                  name: 'item33',
                  active: false,
                },
              ],
            },
          ],  
        }; 

        this.toggleSideNav = this.toggleSideNav.bind(this);
        this.togglePages = this.togglePages.bind(this);
      }


      toggleSideNav() {
        const { sideNav } = this.state;
        sideNav.active = !this.state.sideNav.active
        this.setState({ sideNav });
      }


      togglePages(collection, collTargetId, subpageIndex) {
        let pages = '';
        if (typeof subpageIndex !== 'number' && !subpageIndex) {
          pages = toggleCollectionProperty ({
            arrCollection: collection, 
            targetId: collTargetId, 
            itemIdProp: 'name',
            itemProp: 'active', 
            itemVal: true, 
            itemOppVal: false,
          });
        } else {
          const subpages = toggleCollectionProperty ({
            arrCollection: collection, 
            targetId: collTargetId, 
            itemIdProp: 'name',
            itemProp: 'active', 
            itemVal: true, 
            itemOppVal: false,
          });
          pages = this.state.pages;
          pages[subpageIndex].items = subpages;   
        }

        this.setState({ pages });
      }
    render() {
        return (
            <div className="sidebar">
                <BoxContainer className={`sidebar-menu ${toggleText(this.state.sideNav.active, 'active', '')}`}>
                    {
                        /* Brand and sidebar toggle button */
                    }
                    <TextAndButton
                        className="sidebar-head"
                        btnState={this.state.sideNav.active}
                        handleClick={this.toggleSideNav}  
                        text="Brand Name"
                        brandName={false}

                    />
                    <div className="sidebar-content">
                      <ListComponent
                          data={this.state.pages}
                          Component={
                          (item, itemIndex)=>(
                              <div className={`menu-item ${toggleText(item.active, 'active', '')}`}>
                                <a href="#/"
                                    onClick={()=>this.togglePages(this.state.pages, item.name)}
                                >{ item.title }</a>
                                {
                                    item.items &&
                                    <ul>
                                        <ListComponent
                                        data={item.items}
                                        Component={
                                            (subitem)=>(
                                            <li className={`${toggleText(subitem.active, 'active', '')}`}>
                                                <a href="#/"
                                                onClick={()=>this.togglePages(item.items, subitem.name, itemIndex)}
                                                ><Lefticon></Lefticon>{ subitem.name }</a>
                                            </li>
                                            )
                                        }
                                        />
                                    </ul>
                                }
                              </div>
                          )
                          }
                      />
                    </div>
                </BoxContainer>
            </div>
        );
    }
}

export default LightVersion;
